create database purabali;
use purabali;

-- Tabel master kabupaten
CREATE TABLE kabupaten (
    id_kabupaten VARCHAR(5) PRIMARY KEY,
    nama_kabupaten VARCHAR(50) NOT NULL
);

-- Tabel master jenis pura
CREATE TABLE jenis_pura (
    id_jenis_pura VARCHAR(5) PRIMARY KEY,
    nama_jenis_pura VARCHAR(50) NOT NULL
);

-- Tabel utama pura
CREATE TABLE pura (
    id_pura VARCHAR(5) PRIMARY KEY,
    nama_pura VARCHAR(100) NOT NULL,
    id_jenis_pura VARCHAR(5),
    id_kabupaten VARCHAR(5),
    link_lokasi TEXT,
    latitude DECIMAL(10, 6),
    longitude DECIMAL(10, 6),
    deskripsi_singkat TEXT,
    tahun_berdiri VARCHAR(50),
    link_gambar TEXT,
    FOREIGN KEY (id_jenis_pura) REFERENCES jenis_pura(id_jenis_pura),
    FOREIGN KEY (id_kabupaten) REFERENCES kabupaten(id_kabupaten)
);



